$.editable.addInputType("time",{element:function(settings,original){for(var hourselect=$('<select id="hour_" />'),minselect=$('<select id="min_" />'),hour=0;hour<=23;hour++){hour<10&&(hour="0"+hour);var option=$("<option />").val(hour).append(hour);hourselect.append(option)}$(this).append(hourselect);for(var min=0;min<=45;min=parseInt(min,10)+15){min<10&&(min="0"+min);option=$("<option />").val(min).append(min);minselect.append(option)}$(this).append(minselect);var hidden=$('<input type="hidden" />');return $(this).append(hidden),hidden},content:function(string,settings,original){var hour=parseInt(string.substr(0,2),10),min=parseInt(string.substr(3,2),10);$("#hour_",this).children().each(function(){hour==$(this).val()&&$(this).attr("selected","selected")}),$("#min_",this).children().each(function(){min==$(this).val()&&$(this).attr("selected","selected")})},submit:function(settings,original){var value=$("#hour_").val()+":"+$("#min_").val();$("input",this).val(value)}});